import React from 'react'
import { 
  StyleSheet,
  Text,
  Image,
  TouchableOpacity,
  View,
  StatusBar,
} from 'react-native'
import * as Animatable from 'react-native-animatable';
import {LinearGradient} from 'expo-linear-gradient';
import {Button,Input} from "react-native-elements";
import Material from 'react-native-vector-icons/MaterialCommunityIcons';
import Fontisto from 'react-native-vector-icons/Fontisto';
import Entypo from 'react-native-vector-icons/Entypo';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import {Avatar} from "react-native-elements";

import {screenHeight, screenWidth} from '../config/dimension';

const MainScreen = ({navigation}) => {
  var formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  });
  var total_balance = 2999.99
  return (

    <View style={styles.container}>    
        <StatusBar backgroundColor='#6970E3' barStyle="light-content"/>
        <View style={[styles.header1]}>
        <TouchableOpacity
        onPress={() => navigation.openDrawer()}
        >
          {/* 3 bar menu drop down */}
          <Entypo name='menu' color='white' size={30}/>
          </TouchableOpacity>
        </View>
        <View style={[styles.header2]}>
            <Text style={{fontSize:40,fontWeight: 'bold', color:'#ffffff',textAlign: 'center'}}>{formatter.format(total_balance)}</Text>
            <Text style={{fontSize:16,color:'#F8F9FA',alignItems: 'center',textAlign: 'center'}}> Available Balance</Text>
    
        </View>
        <Animatable.View 
            animation="fadeInUpBig"
            style={styles.footer}
        >
        <View style={styles.button}>
        <TouchableOpacity
                    style={styles.b1}
                    onPress={() => navigation.navigate('TransferScreen')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <Material name='bank-transfer' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>Money Transfer</Text>
                </LinearGradient>
                </TouchableOpacity>
        <TouchableOpacity
                    style={styles.b1}
                   // onPress={() => navigation.navigate('......')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <FontAwesome name='money' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>Add Money</Text>
                </LinearGradient>
                </TouchableOpacity>
        <TouchableOpacity
                    style={styles.b1}
                  // onPress={() => navigation.navigate('......')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <Entypo name='shop' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>E-Shopping</Text>
                </LinearGradient>
                </TouchableOpacity>
        <TouchableOpacity
                    style={styles.b1}
                   // onPress={() => navigation.navigate('.....')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <Entypo name='credit-card' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>Add Card</Text>
                </LinearGradient>
                </TouchableOpacity>
        <TouchableOpacity
                    style={styles.b1}
                   // onPress={() => navigation.navigate('.....')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <Entypo name='wallet' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}> Modify Wallet</Text>
                </LinearGradient>
                </TouchableOpacity>
        <TouchableOpacity
                    style={styles.b1}
                   //onPress={() => navigation.navigate('')}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.b1}
                >
                <Material name='account-box-outline' color='#ffffff' size={50}/>
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>Account Status</Text>
                </LinearGradient>
                </TouchableOpacity>
      </View>
      {/* <TouchableOpacity
                    onPress={() => navigation.navigate('LoginScreen')}
                    style={[styles.blogout,{
                      marginBottom: 30
                    }]}
                >
                <LinearGradient
                    colors={['#696FE2', '#7158B7']}
                    style={styles.blogout}
                >
                    <Text style={[styles.text1, {
                        color:'#fff'
                    }]}>Log Out</Text>
                </LinearGradient>
                </TouchableOpacity> */}

        </Animatable.View>
        </View>
  
  );
};

export default MainScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#6970E3'
  },
  header1: {
      flex: 0.15,
      //justifyContent: 'flex-end',
      flexDirection:'row',
      paddingHorizontal: 30,
      paddingBottom: 2,
      marginTop:50,
      //justifyContent: 'center',
  },
  header2: {
    flex: 0.45,
   // justifyContent: 'flex-end',
   flexDirection:'column',
    paddingBottom: 2,
    justifyContent: 'center',
},
  footer: {
      flex: 3,
      backgroundColor: '#fff',
      borderTopLeftRadius: 30,
      borderTopRightRadius: 30,
      paddingHorizontal: 35,
      paddingVertical: 40
  },
  text_header: {
      color: '#fff',
      fontWeight: 'bold',
      alignContent:'center',
      justifyContent: 'center',
      fontSize: 24
  },
  text_footer: {
      color: '#05375a',
      fontSize: 18
  },
  action: {
      flexDirection: 'row',
      marginTop: 10,
      borderBottomWidth: 1,
      borderBottomColor: '#f2f2f2',
      paddingBottom: 5
  },
  actionError: {
      flexDirection: 'row',
      marginTop: 10,
      borderBottomWidth: 1,
      borderBottomColor: '#FF0000',
      paddingBottom: 5
  },
  textInput: {
      flex: 1,
      marginTop: 0,
      paddingLeft: 10,
      fontSize: 16,
      color: '#05375a',
  },
  errorMsg: {
      color: '#FF0000',
      fontSize: 14,
  },
  button:{
    flex: 3.5,
    flexDirection:'row',
    flexWrap:'wrap',
    height:'100%',
  },
  icon_header:{
   // flex:1,
    //flexDirection:'row',
    //justifyContent:'space-between',
    //position: 'absolute',
    // marginLeft:10,
    // marginTop:50

  },
  b1: {
      width:0.36 * screenWidth,
      height:0.16 * screenHeight,
      margin: 10,
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: 10
  },
  blogout: {
    width: '100%',
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 10
  },
  text1: {
      fontSize: 16,
      fontWeight: 'bold'
  }
});
